/* eslint-disable consistent-return */
import chai from 'chai';
import chaiHttp from 'chai-http';
import app from '../app';
import { codes, messages } from '../helpers/messages-and-codes';

const { should } = chai;

should();
chai.use(chaiHttp);

/* global describe, it */
describe('User creating an account', () => {
  it('should return status 201 and a data object containing new user info', done => {
    chai
      .request(app)
      .post('/api/auth/signup')
      .send({
        first_name: 'Daniel',
        last_name: 'Chapelle',
        email: 'danielchapelle@gmail.com',
        phone_number: '0788543666',
        password: 'danielpassword',
        confirm_password: 'danielpassword'
      })
      .end((err, res) => {
        if (err) return done(err);

        res.body.should.be.a('object');
        res.body.should.include.keys(['status', 'data']);
        res.body.status.should.be.a('number');
        res.body.status.should.equal(codes.resourceCreated);
        res.body.data.should.be.a('object');
        res.body.data.should.include.keys([
          'first_name',
          'last_name',
          'email',
          'phone_number',
          'password',
          'is_admin'
        ]);
      });
    done();
  });

  it('should return status 409 if the user already exists', done => {
    chai
      .request(app)
      .post('/api/auth/signup')
      .send({
        first_name: 'Daniel',
        last_name: 'Chapelle',
        email: 'danielchapelle@gmail.com',
        password: 'danielpassword',
        phone_number: '0788543666',
        confirm_password: 'danielpassword'
      })
      .end((err, res) => {
        if (err) return done(err);

        res.body.should.be.a('object');
        res.body.should.include.keys(['status', 'error']);
        res.body.status.should.be.a('number');
        res.body.status.should.equal(codes.conflict);
        res.body.error.should.be.a('string');
        res.body.error.should.equal(messages.userExists);
      });
    done();
  });

  it('should return status 400 if the user input is wrong or incomplete', done => {
    chai
      .request(app)
      .post('/api/auth/signup')
      .send({
        first_name: 'Daniel',
        last_name: 'Chapelle',
        password: 'danielpassword',
        phone_number: '0788543666',
        confirm_password: 'danielpassword'
      })
      .end((err, res) => {
        if (err) return done(err);

        res.body.should.be.a('object');
        res.body.should.include.keys(['status', 'error']);
        res.body.status.should.be.a('number');
        res.body.status.should.equal(codes.badRequest);
        res.body.error.should.be.a('string');
      });
    done();
  });
});

/* eslint-disable no-console */
import express from 'express';
import dotenv from 'dotenv';
import bodyParser from 'body-parser';
import morgan from 'morgan';
import router from './routes/mainRouter';

dotenv.config();

const app = express();

app.use(bodyParser.json());
app.use(morgan('dev'));

app.use(router);

const port = process.env.PORT || 5000;

app.listen(port, () => {
  console.log(`We're listening on port ${port}`);
});

export default app;

import express from 'express';
import usersRouter from './usersRouter';

const router = express.Router();

router.use('/api', usersRouter);

export default router;

import express from 'express';
import userSignup from '../controllers/users';

const userRouter = express.Router();

userRouter.post('/auth/signup', userSignup);

export default userRouter;

import bcrypt from 'bcrypt';
import validateUserSignup from '../helpers/validation';
import { codes, messages } from '../helpers/messages-and-codes';
import { retrieveUser, createUser } from '../helpers/queries';

const userSignUp = async (req, res) => {
  const { error, value } = await validateUserSignup(req.body);

  // Return error 400, if user input validation fails
  if (error)
    return res
      .status(codes.badRequest)
      .json({ status: res.statusCode, error: error.message });

  // Check if the user already exists
  const userExists = await retrieveUser('email', value.email);
  if (userExists.rows.length > 0)
    return res
      .status(codes.conflict)
      .json({ status: res.statusCode, error: messages.userExists });

  // Hash password
  const saltRounds = 10;
  const salt = await bcrypt.genSalt(saltRounds);
  const hashedPassword = await bcrypt.hash(value.password, salt);

  // Create user instance in the database
  const user = await createUser(value, hashedPassword);

  return res
    .status(codes.resourceCreated)
    .json({ status: res.statusCode, data: user.rows[0] });
};

export default userSignUp;

import Joi from '@hapi/joi';

const validateUserSignup = data => {
  const schema = Joi.object({
    first_name: Joi.string().required(),
    last_name: Joi.string().required(),
    email: Joi.string()
      .email()
      .required(),
    phone_number: Joi.string().required(),
    password: Joi.string()
      .alphanum()
      .required(),
    confirm_password: Joi.ref('password')
  });

  return schema.validate(data);
};

export default validateUserSignup;

const codes = {
  badRequest: 400,
  conflict: 409,
  resourceCreated: 201,
  unauthorized: 401,
  okay: 200,
  notFound: 404
};

const messages = {
  userExists: 'You already have an account. Proceed with sign in instead'
};

export { codes, messages };

import pool from '../data/config';

const retrieveUser = async (columns, value) => {
  try {
    const client = await pool.connect();
    const data = await client.query(
      `SELECT ${columns} FROM users WHERE email = $1`,
      [value]
    );
    client.release();
    return data;
  } catch (e) {
    return e.stack;
  }
};

const createUser = async (data, hashedPassword) => {
  const client = await pool.connect();
  await client.query(
    `INSERT INTO users (first_name, last_name, email, phone_number, password, is_admin) 
    VALUES($1, $2, $3, $4, $5, $6)
    `,
    [
      data.first_name,
      data.last_name,
      data.email,
      data.phone_number,
      hashedPassword,
      false
    ]
  );
  const user = await retrieveUser('*', data.email);

  client.release();

  return user;
};

export { retrieveUser, createUser };

import { Pool } from 'pg';
import dotenv from 'dotenv';

dotenv.config();

let connectionString = '';

if (process.env.NODE_ENV === 'production') {
  connectionString = process.env.DB_URL;
} else if (process.env.NODE_ENV === 'dev') {
  connectionString = `postgresql://${process.env.DB_USER}:${process.env.DB_PASSWORD}@${process.env.DB_HOST}:${process.env.DB_PORT}/${process.env.DB_DEV}`;
} else if (process.env.NODE_ENV === 'test') {
  connectionString = `postgresql://${process.env.DB_USER}:${process.env.DB_PASSWORD}@${process.env.DB_HOST}:${process.env.DB_PORT}/${process.env.DB_TEST}`;
}

const pool = new Pool({ connectionString });

export default pool;
